mapper.select:
select
  id, address_id, address_seq_no, province_code, province_name, city_code, city_name, district_code, district_name, post_code, detail_address, remarks, insert_time, timestamp
from comm_address


        and id = #{id}


        and address_id = #{addressId}


        and address_seq_no = #{addressSeqNo}


        and province_code = #{provinceCode}


        and province_name = #{provinceName}


        and city_code = #{cityCode}


        and city_name = #{cityName}


        and district_code = #{districtCode}


        and district_name = #{districtName}


        and post_code = #{postCode}


        and detail_address = #{detailAddress}


        and remarks = #{remarks}


        and insert_time = #{insertTime}


        and timestamp = #{timestamp}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from comm_address


        and id = #{id}


        and address_id = #{addressId}


        and address_seq_no = #{addressSeqNo}


        and province_code = #{provinceCode}


        and province_name = #{provinceName}


        and city_code = #{cityCode}


        and city_name = #{cityName}


        and district_code = #{districtCode}


        and district_name = #{districtName}


        and post_code = #{postCode}


        and detail_address = #{detailAddress}


        and remarks = #{remarks}


        and insert_time = #{insertTime}


        and timestamp = #{timestamp}
