mapper.select:
select * from comm_reference where 1 = 1

    and type = #{type}


    and code = #{code}


    and language_id = #{languageId}


    and category_cd = #{categoryCd}


    and code_desc = #{codeDesc}


    and last_value = #{lastValue}


    and span = #{span}


    and remarks = #{remarks}

mapper.update:
update comm_reference
set timestamp = #{timestamp}

    , last_value = last_value +span

where type = #{type}
and code = #{code}
